mapper.sql:
select * from sys_user where 1=1

    and user_name like concat('%',#{username},'%')


    and user_realname like  concat('%',#{realName},'%')


    and user_state = #{state}


    and user_scope = #{userScope}


    and user_type = #{userType}

mapper.insert:
insert into sys_user set user_name = #{username} , user_realname = #{realName} , password = #{password}
, telphone = #{telphone} , department = #{department} , user_scope = #{userScope} , user_type = #{userType}
, user_state = #{state},create_time = #{createTime}

mapper.update:
 update sys_user set  user_realname = #{realName} , password = #{password}
, telphone = #{telphone} , department = #{department} , user_scope = #{userScope} , user_type = #{userType}
, user_state = #{state} where user_name = #{username}

mapper.select:
select id,user_name,user_realname,department, case when user_scope =1 then '内部用户' else '外部用户' end as user_scope, case when user_type =1 then '长期用户' else '单次用户' end as user_type, case when user_state =1 then '已启用' else '已停用' end as user_state, create_time from sys_user where 1=1 and isdelete = 0 /* mybatis 中 模糊查询 不支持 username != '' */
and user_name like concat('%',#{user.username},'%')
and user_realname like concat('%',#{user.realName},'%')
and UNIX_TIMESTAMP(#{startT}) <= UNIX_TIMESTAMP(create_time)
and UNIX_TIMESTAMP(#{endT}) >= UNIX_TIMESTAMP(create_time)
and user_state = #{user.state}
and user_scope = #{user.userScope}
and user_type = #{user.userType}
order by id
